import React, { useState } from 'react';
import { useQuery } from 'react-query';
import { api } from '../../services/api';
import LoadingSpinner from '../../components/common/LoadingSpinner';

const QuizHistory = () => {
  const [selectedSubmission, setSelectedSubmission] = useState(null);
  const [showDetailsModal, setShowDetailsModal] = useState(false);

  // Fetch submission history
  const { data: submissions, isLoading, error } = useQuery(
    'my-submissions',
    () => api.get('/submissions/my-submissions')
  );

  const getScoreColor = (score, totalScore) => {
    const percentage = (score / totalScore) * 100;
    if (percentage >= 90) return 'text-success';
    if (percentage >= 70) return 'text-warning';
    return 'text-danger';
  };

  const getGradeBadge = (score, totalScore) => {
    const percentage = (score / totalScore) * 100;
    if (percentage >= 90) return <span className="badge bg-success">A</span>;
    if (percentage >= 80) return <span className="badge bg-info">B</span>;
    if (percentage >= 70) return <span className="badge bg-warning">C</span>;
    if (percentage >= 60) return <span className="badge bg-secondary">D</span>;
    return <span className="badge bg-danger">F</span>;
  };

  const formatDuration = (seconds) => {
    const hours = Math.floor(seconds / 3600);
    const minutes = Math.floor((seconds % 3600) / 60);
    const secs = seconds % 60;

    if (hours > 0) {
      return `${hours}h ${minutes}m ${secs}s`;
    }
    return `${minutes}m ${secs}s`;
  };

  if (isLoading) return <LoadingSpinner />;
  if (error) return <div className="alert alert-danger">Error loading quiz history</div>;

  return (
    <div className="container-fluid">
      <div className="row">
        <div className="col-12">
          <h1>Quiz History</h1>

          {/* Summary Cards */}
          <div className="row mb-4">
            <div className="col-md-3">
              <div className="card text-center">
                <div className="card-body">
                  <h3 className="text-primary">{submissions?.length || 0}</h3>
                  <p className="card-text">Total Quizzes</p>
                </div>
              </div>
            </div>
            <div className="col-md-3">
              <div className="card text-center">
                <div className="card-body">
                  <h3 className="text-success">
                    {submissions?.filter(s => s.score >= s.total_score * 0.7).length || 0}
                  </h3>
                  <p className="card-text">Passed</p>
                </div>
              </div>
            </div>
            <div className="col-md-3">
              <div className="card text-center">
                <div className="card-body">
                  <h3 className="text-info">
                    {submissions?.length > 0 
                      ? Math.round(submissions.reduce((acc, s) => acc + (s.score / s.total_score * 100), 0) / submissions.length)
                      : 0}%
                  </h3>
                  <p className="card-text">Average Score</p>
                </div>
              </div>
            </div>
            <div className="col-md-3">
              <div className="card text-center">
                <div className="card-body">
                  <h3 className="text-warning">
                    {submissions?.reduce((acc, s) => acc + s.time_taken, 0) || 0}
                  </h3>
                  <p className="card-text">Total Time (min)</p>
                </div>
              </div>
            </div>
          </div>

          {/* Submissions Table */}
          <div className="card">
            <div className="card-header">
              <h5>Quiz Submissions</h5>
            </div>
            <div className="card-body">
              <div className="table-responsive">
                <table className="table table-striped table-hover">
                  <thead>
                    <tr>
                      <th>Quiz</th>
                      <th>Class</th>
                      <th>Score</th>
                      <th>Grade</th>
                      <th>Time Taken</th>
                      <th>Submitted</th>
                      <th>Actions</th>
                    </tr>
                  </thead>
                  <tbody>
                    {submissions?.map((submission) => (
                      <tr key={submission.id}>
                        <td>
                          <strong>{submission.quiz_title}</strong>
                          <br />
                          <small className="text-muted">{submission.quiz_type}</small>
                        </td>
                        <td>{submission.class_name}</td>
                        <td>
                          <span className={getScoreColor(submission.score, submission.total_score)}>
                            <strong>{submission.score}/{submission.total_score}</strong>
                          </span>
                          <br />
                          <small className="text-muted">
                            {Math.round((submission.score / submission.total_score) * 100)}%
                          </small>
                        </td>
                        <td>
                          {getGradeBadge(submission.score, submission.total_score)}
                        </td>
                        <td>{formatDuration(submission.time_taken)}</td>
                        <td>
                          {new Date(submission.submitted_at).toLocaleDateString()}
                          <br />
                          <small className="text-muted">
                            {new Date(submission.submitted_at).toLocaleTimeString()}
                          </small>
                        </td>
                        <td>
                          <button
                            className="btn btn-sm btn-outline-primary"
                            onClick={() => {
                              setSelectedSubmission(submission);
                              setShowDetailsModal(true);
                            }}
                          >
                            <i className="bi bi-eye me-1"></i>
                            View Details
                          </button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>

              {(!submissions || submissions.length === 0) && (
                <div className="text-center py-5">
                  <i className="bi bi-journal-x display-1 text-muted"></i>
                  <h3 className="mt-3">No quiz history</h3>
                  <p className="text-muted">You haven't taken any quizzes yet.</p>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>

      {/* Details Modal */}
      {showDetailsModal && selectedSubmission && (
        <div className="modal fade show" style={{ display: 'block' }} tabIndex="-1">
          <div className="modal-dialog modal-lg">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">
                  Quiz Details - {selectedSubmission.quiz_title}
                </h5>
                <button
                  type="button"
                  className="btn-close"
                  onClick={() => setShowDetailsModal(false)}
                ></button>
              </div>
              <div className="modal-body">
                <div className="row">
                  <div className="col-md-6">
                    <h6>Quiz Information</h6>
                    <p><strong>Title:</strong> {selectedSubmission.quiz_title}</p>
                    <p><strong>Class:</strong> {selectedSubmission.class_name}</p>
                    <p><strong>Type:</strong> {selectedSubmission.quiz_type}</p>
                    <p><strong>Duration:</strong> {selectedSubmission.quiz_duration} minutes</p>
                  </div>
                  <div className="col-md-6">
                    <h6>Your Performance</h6>
                    <p>
                      <strong>Score:</strong> 
                      <span className={getScoreColor(selectedSubmission.score, selectedSubmission.total_score)}>
                        {selectedSubmission.score}/{selectedSubmission.total_score}
                      </span>
                    </p>
                    <p>
                      <strong>Percentage:</strong> 
                      {Math.round((selectedSubmission.score / selectedSubmission.total_score) * 100)}%
                    </p>
                    <p><strong>Grade:</strong> {getGradeBadge(selectedSubmission.score, selectedSubmission.total_score)}</p>
                    <p><strong>Time Taken:</strong> {formatDuration(selectedSubmission.time_taken)}</p>
                  </div>
                </div>

                <hr />

                <div className="row">
                  <div className="col-12">
                    <h6>Submission Details</h6>
                    <p><strong>Submitted:</strong> {new Date(selectedSubmission.submitted_at).toLocaleString()}</p>
                    <p><strong>Attempt:</strong> {selectedSubmission.attempt_number}</p>

                    {selectedSubmission.feedback && (
                      <div className="mt-3">
                        <h6>Feedback</h6>
                        <div className="alert alert-info">
                          {selectedSubmission.feedback}
                        </div>
                      </div>
                    )}
                  </div>
                </div>
              </div>
              <div className="modal-footer">
                <button
                  type="button"
                  className="btn btn-secondary"
                  onClick={() => setShowDetailsModal(false)}
                >
                  Close
                </button>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Modal Backdrop */}
      {showDetailsModal && (
        <div className="modal-backdrop fade show"></div>
      )}
    </div>
  );
};

export default QuizHistory;
