import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';

const Navbar = () => {
  const { user, logout } = useAuth();
  const location = useLocation();

  const isActive = (path) => {
    return location.pathname === path || location.pathname.startsWith(path + '/');
  };

  const handleLogout = async () => {
    await logout();
  };

  const getNavLinks = () => {
    const commonLinks = [
      { path: '/dashboard', label: 'Dashboard', icon: 'bi-speedometer2' },
      { path: '/profile', label: 'Profile', icon: 'bi-person' },
    ];

    const roleLinks = {
      admin: [
        { path: '/admin/users', label: 'User Management', icon: 'bi-people' },
        { path: '/admin/analytics', label: 'Analytics', icon: 'bi-bar-chart' },
        { path: '/admin/audit-logs', label: 'Audit Logs', icon: 'bi-file-text' },
      ],
      professor: [
        { path: '/professor/classes', label: 'Classes', icon: 'bi-book' },
        { path: '/professor/quizzes', label: 'Quizzes', icon: 'bi-question-circle' },
      ],
      student: [
        { path: '/student/quizzes', label: 'Available Quizzes', icon: 'bi-list-check' },
        { path: '/student/history', label: 'Quiz History', icon: 'bi-clock-history' },
      ],
    };

    return [...commonLinks, ...(roleLinks[user?.role] || [])];
  };

  return (
    <nav className="navbar navbar-expand-lg navbar-dark bg-primary">
      <div className="container-fluid">
        <Link className="navbar-brand" to="/dashboard">
          <i className="bi bi-mortarboard me-2"></i>
          Quiz Management
        </Link>

        <button
          className="navbar-toggler"
          type="button"
          data-bs-toggle="collapse"
          data-bs-target="#navbarNav"
          aria-controls="navbarNav"
          aria-expanded="false"
          aria-label="Toggle navigation"
        >
          <span className="navbar-toggler-icon"></span>
        </button>

        <div className="collapse navbar-collapse" id="navbarNav">
          <ul className="navbar-nav me-auto">
            {getNavLinks().map((link) => (
              <li key={link.path} className="nav-item">
                <Link
                  className={`nav-link ${isActive(link.path) ? 'active' : ''}`}
                  to={link.path}
                >
                  <i className={`${link.icon} me-1`}></i>
                  {link.label}
                </Link>
              </li>
            ))}
          </ul>

          <ul className="navbar-nav">
            <li className="nav-item dropdown">
              <a
                className="nav-link dropdown-toggle"
                href="#"
                id="navbarDropdown"
                role="button"
                data-bs-toggle="dropdown"
                aria-expanded="false"
              >
                <i className="bi bi-person-circle me-1"></i>
                {user?.name || user?.email}
                <span className="badge bg-light text-dark ms-2">
                  {user?.role}
                </span>
              </a>
              <ul className="dropdown-menu" aria-labelledby="navbarDropdown">
                <li>
                  <Link className="dropdown-item" to="/profile">
                    <i className="bi bi-person me-2"></i>
                    Profile
                  </Link>
                </li>
                <li><hr className="dropdown-divider" /></li>
                <li>
                  <button className="dropdown-item" onClick={handleLogout}>
                    <i className="bi bi-box-arrow-right me-2"></i>
                    Logout
                  </button>
                </li>
              </ul>
            </li>
          </ul>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
