import React from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import { useAuth } from './context/AuthContext';

// Common components
import Navbar from './components/common/Navbar';
import LoadingSpinner from './components/common/LoadingSpinner';
import ProtectedRoute from './components/common/ProtectedRoute';

// Auth components
import LoginPage from './pages/LoginPage';
import RegisterPage from './pages/RegisterPage';

// Admin components
import AdminDashboard from './pages/admin/AdminDashboard';
import UserManagement from './pages/admin/UserManagement';
import SystemAnalytics from './pages/admin/SystemAnalytics';
import AuditLogs from './pages/admin/AuditLogs';

// Professor components
import ProfessorDashboard from './pages/professor/ProfessorDashboard';
import ClassManagement from './pages/professor/ClassManagement';
import QuizManagement from './pages/professor/QuizManagement';
import QuizCreator from './pages/professor/QuizCreator';
import QuizResults from './pages/professor/QuizResults';

// Student components
import StudentDashboard from './pages/student/StudentDashboard';
import AvailableQuizzes from './pages/student/AvailableQuizzes';
import TakeQuiz from './pages/student/TakeQuiz';
import QuizHistory from './pages/student/QuizHistory';

// Shared components
import Profile from './pages/Profile';
import NotFound from './pages/NotFound';

import './App.css';

function App() {
  const { user, loading } = useAuth();

  if (loading) {
    return (
      <div className="d-flex justify-content-center align-items-center vh-100">
        <LoadingSpinner />
      </div>
    );
  }

  return (
    <div className="App">
      {user && <Navbar />}
      <div className={user ? "container-fluid mt-4" : ""}>
        <Routes>
          {/* Public Routes */}
          <Route 
            path="/login" 
            element={!user ? <LoginPage /> : <Navigate to="/dashboard" replace />} 
          />
          <Route 
            path="/register" 
            element={!user ? <RegisterPage /> : <Navigate to="/dashboard" replace />} 
          />

          {/* Protected Routes */}
          <Route path="/" element={
            <ProtectedRoute>
              <Navigate to="/dashboard" replace />
            </ProtectedRoute>
          } />

          {/* Dashboard Routes (Role-based) */}
          <Route path="/dashboard" element={
            <ProtectedRoute>
              {user?.role === 'admin' && <AdminDashboard />}
              {user?.role === 'professor' && <ProfessorDashboard />}
              {user?.role === 'student' && <StudentDashboard />}
            </ProtectedRoute>
          } />

          {/* Admin Routes */}
          <Route path="/admin/*" element={
            <ProtectedRoute requiredRole="admin">
              <Routes>
                <Route index element={<AdminDashboard />} />
                <Route path="users" element={<UserManagement />} />
                <Route path="analytics" element={<SystemAnalytics />} />
                <Route path="audit-logs" element={<AuditLogs />} />
              </Routes>
            </ProtectedRoute>
          } />

          {/* Professor Routes */}
          <Route path="/professor/*" element={
            <ProtectedRoute requiredRole="professor">
              <Routes>
                <Route index element={<ProfessorDashboard />} />
                <Route path="classes" element={<ClassManagement />} />
                <Route path="quizzes" element={<QuizManagement />} />
                <Route path="quizzes/create" element={<QuizCreator />} />
                <Route path="quizzes/:id/edit" element={<QuizCreator />} />
                <Route path="quizzes/:id/results" element={<QuizResults />} />
              </Routes>
            </ProtectedRoute>
          } />

          {/* Student Routes */}
          <Route path="/student/*" element={
            <ProtectedRoute requiredRole="student">
              <Routes>
                <Route index element={<StudentDashboard />} />
                <Route path="quizzes" element={<AvailableQuizzes />} />
                <Route path="quizzes/:id/take" element={<TakeQuiz />} />
                <Route path="history" element={<QuizHistory />} />
              </Routes>
            </ProtectedRoute>
          } />

          {/* Shared Routes */}
          <Route path="/profile" element={
            <ProtectedRoute>
              <Profile />
            </ProtectedRoute>
          } />

          {/* Catch all route */}
          <Route path="*" element={<NotFound />} />
        </Routes>
      </div>
    </div>
  );
}

export default App;
