import React from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import ProtectedRoute from './components/common/ProtectedRoute';
import LoadingSpinner from './components/common/LoadingSpinner';
import Navbar from './components/common/Navbar';
import DebugAuth from './components/DebugAuth';
import { useAuth } from './context/AuthContext';

// Auth Components
import Login from './components/auth/Login';
import Register from './components/auth/Register';

// CSS
import './App.css';

// Debug component to show authentication state
const AuthDebug = () => {
    const { user, token, loading } = useAuth();

    if (process.env.NODE_ENV !== 'development') {
        return null;
    }

    return (
        <div style={{ 
            position: 'fixed', 
            top: 0, 
            right: 0, 
            background: 'rgba(0,0,0,0.8)', 
            color: 'white', 
            padding: '10px', 
            fontSize: '12px',
            zIndex: 9999,
            maxWidth: '300px'
        }}>
            <strong>Auth Debug:</strong><br/>
            Loading: {loading ? 'true' : 'false'}<br/>
            User: {user ? user.username : 'null'}<br/>
            Role: {user?.role || 'none'}<br/>
            Token: {token ? 'exists' : 'none'}<br/>
            LocalStorage: {localStorage.getItem('token') ? 'exists' : 'none'}
        </div>
    );
};

// Default route component that redirects based on auth state
const DefaultRoute = () => {
    const { user, loading } = useAuth();

    if (loading) {
        return <LoadingSpinner />;
    }

    if (!user) {
        return <Navigate to="/login" replace />;
    }

    // Redirect to appropriate dashboard based on role
    if (user.role === 'admin') {
        return <Navigate to="/admin/dashboard" replace />;
    } else if (user.role === 'professor') {
        return <Navigate to="/professor/dashboard" replace />;
    } else {
        return <Navigate to="/student/dashboard" replace />;
    }
};

// Basic Dashboard Components (temporary)
const AdminDashboard = () => (
    <div className="container mt-4">
        <h2>Admin Dashboard</h2>
        <p>Welcome to the admin dashboard!</p>
        <p>This page should only be visible to admin users.</p>
    </div>
);

const ProfessorDashboard = () => (
    <div className="container mt-4">
        <h2>Professor Dashboard</h2>
        <p>Welcome to the professor dashboard!</p>
        <p>This page should only be visible to professor users.</p>
    </div>
);

const StudentDashboard = () => (
    <div className="container mt-4">
        <h2>Student Dashboard</h2>
        <p>Welcome to the student dashboard!</p>
        <p>This page should only be visible to student users.</p>
    </div>
);

function App() {
    return (
        <div className="App">
            <AuthDebug />
            <Navbar />
            <Routes>
                {/* Public Routes */}
                <Route path="/login" element={<Login />} />
                <Route path="/register" element={<Register />} />

                {/* Debug Route (development only) */}
                <Route path="/debug" element={<DebugAuth />} />

                {/* Default redirect based on auth state */}
                <Route path="/" element={<DefaultRoute />} />

                {/* Admin Routes */}
                <Route
                    path="/admin/dashboard"
                    element={
                        <ProtectedRoute requiredRole="admin">
                            <AdminDashboard />
                        </ProtectedRoute>
                    }
                />

                {/* Professor Routes */}
                <Route
                    path="/professor/dashboard"
                    element={
                        <ProtectedRoute requiredRole="professor">
                            <ProfessorDashboard />
                        </ProtectedRoute>
                    }
                />

                {/* Student Routes */}
                <Route
                    path="/student/dashboard"
                    element={
                        <ProtectedRoute requiredRole="student">
                            <StudentDashboard />
                        </ProtectedRoute>
                    }
                />

                {/* Catch all - redirect to default route */}
                <Route path="*" element={<Navigate to="/" replace />} />
            </Routes>
        </div>
    );
}

export default App;
