import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify';
import { api } from '../../services/api';

const QuizCreator = () => {
  const [quiz, setQuiz] = useState({
    title: '',
    description: '',
    quiz_type: 'quiz',
    duration: 60,
    max_attempts: 1,
    start_date: '',
    end_date: '',
    instructions: '',
    class_id: ''
  });
  const [questions, setQuestions] = useState([]);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const response = await api.post('/quizzes', {
        ...quiz,
        questions
      });
      toast.success('Quiz created successfully');
      navigate('/professor/quizzes');
    } catch (error) {
      toast.error(error.response?.data?.message || 'Failed to create quiz');
    } finally {
      setLoading(false);
    }
  };

  const addQuestion = () => {
    setQuestions([
      ...questions,
      {
        question_text: '',
        question_type: 'multiple_choice',
        options: ['', '', '', ''],
        correct_answer: '',
        points: 1
      }
    ]);
  };

  const removeQuestion = (index) => {
    setQuestions(questions.filter((_, i) => i !== index));
  };

  const updateQuestion = (index, field, value) => {
    const updated = [...questions];
    updated[index][field] = value;
    setQuestions(updated);
  };

  return (
    <div className="container-fluid">
      <div className="row">
        <div className="col-12">
          <h1>Create Quiz</h1>

          <form onSubmit={handleSubmit}>
            <div className="card mb-4">
              <div className="card-header">
                <h5>Basic Information</h5>
              </div>
              <div className="card-body">
                <div className="row">
                  <div className="col-md-6">
                    <div className="mb-3">
                      <label className="form-label">Quiz Title</label>
                      <input
                        type="text"
                        className="form-control"
                        value={quiz.title}
                        onChange={(e) => setQuiz({...quiz, title: e.target.value})}
                        required
                      />
                    </div>
                  </div>
                  <div className="col-md-6">
                    <div className="mb-3">
                      <label className="form-label">Quiz Type</label>
                      <select
                        className="form-select"
                        value={quiz.quiz_type}
                        onChange={(e) => setQuiz({...quiz, quiz_type: e.target.value})}
                      >
                        <option value="quiz">Quiz</option>
                        <option value="poll">Poll</option>
                        <option value="survey">Survey</option>
                      </select>
                    </div>
                  </div>
                </div>
                <div className="mb-3">
                  <label className="form-label">Description</label>
                  <textarea
                    className="form-control"
                    rows="3"
                    value={quiz.description}
                    onChange={(e) => setQuiz({...quiz, description: e.target.value})}
                  />
                </div>
                <div className="row">
                  <div className="col-md-6">
                    <div className="mb-3">
                      <label className="form-label">Duration (minutes)</label>
                      <input
                        type="number"
                        className="form-control"
                        value={quiz.duration}
                        onChange={(e) => setQuiz({...quiz, duration: parseInt(e.target.value)})}
                        min="1"
                      />
                    </div>
                  </div>
                  <div className="col-md-6">
                    <div className="mb-3">
                      <label className="form-label">Max Attempts</label>
                      <input
                        type="number"
                        className="form-control"
                        value={quiz.max_attempts}
                        onChange={(e) => setQuiz({...quiz, max_attempts: parseInt(e.target.value)})}
                        min="1"
                      />
                    </div>
                  </div>
                </div>
              </div>
            </div>

            <div className="card mb-4">
              <div className="card-header d-flex justify-content-between">
                <h5>Questions</h5>
                <button
                  type="button"
                  className="btn btn-sm btn-primary"
                  onClick={addQuestion}
                >
                  Add Question
                </button>
              </div>
              <div className="card-body">
                {questions.map((question, index) => (
                  <div key={index} className="border p-3 mb-3 rounded">
                    <div className="d-flex justify-content-between mb-2">
                      <h6>Question {index + 1}</h6>
                      <button
                        type="button"
                        className="btn btn-sm btn-outline-danger"
                        onClick={() => removeQuestion(index)}
                      >
                        Remove
                      </button>
                    </div>
                    <div className="mb-3">
                      <input
                        type="text"
                        className="form-control"
                        placeholder="Question text"
                        value={question.question_text}
                        onChange={(e) => updateQuestion(index, 'question_text', e.target.value)}
                      />
                    </div>
                    <div className="row">
                      <div className="col-md-6">
                        <select
                          className="form-select"
                          value={question.question_type}
                          onChange={(e) => updateQuestion(index, 'question_type', e.target.value)}
                        >
                          <option value="multiple_choice">Multiple Choice</option>
                          <option value="true_false">True/False</option>
                          <option value="text">Text Answer</option>
                        </select>
                      </div>
                      <div className="col-md-6">
                        <input
                          type="number"
                          className="form-control"
                          placeholder="Points"
                          value={question.points}
                          onChange={(e) => updateQuestion(index, 'points', parseInt(e.target.value))}
                          min="1"
                        />
                      </div>
                    </div>
                    {question.question_type === 'multiple_choice' && (
                      <div className="mt-3">
                        {question.options.map((option, optionIndex) => (
                          <div key={optionIndex} className="mb-2">
                            <input
                              type="text"
                              className="form-control"
                              placeholder={`Option ${optionIndex + 1}`}
                              value={option}
                              onChange={(e) => {
                                const newOptions = [...question.options];
                                newOptions[optionIndex] = e.target.value;
                                updateQuestion(index, 'options', newOptions);
                              }}
                            />
                          </div>
                        ))}
                      </div>
                    )}
                  </div>
                ))}
                {questions.length === 0 && (
                  <div className="text-center py-4">
                    <p className="text-muted">No questions added yet. Click "Add Question" to start.</p>
                  </div>
                )}
              </div>
            </div>

            <div className="d-flex justify-content-between">
              <button
                type="button"
                className="btn btn-secondary"
                onClick={() => navigate('/professor/quizzes')}
              >
                Cancel
              </button>
              <button
                type="submit"
                className="btn btn-primary"
                disabled={loading}
              >
                {loading ? 'Creating...' : 'Create Quiz'}
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default QuizCreator;
